<div class="modal">
  <div class="modal-container">
    <div class="modal-left">
      <h1 class="modal-title">Welcome!</h1>
      <p class="modal-desc">Budget Planner Application where you can manage , control and observe your Financials</p>
      <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
        <div class="input-block">
          <label for="register-email" class="input-label">Email</label>
          <input type="email" name="register-email" id="register-email" placeholder="Email" formControlName="email">
          <div *ngIf="registerForm.get('email')?.errors && (registerForm.get('email')?.dirty || registerForm.get('email')?.touched)" class="error">
            <div *ngIf="registerForm.get('email')?.errors?.required">Email is required.</div>
            <div *ngIf="registerForm.get('email')?.errors?.email">Invalid email format.</div>
          </div>
        </div>
        <div class="input-block">
          <label for="register-password" class="input-label">Password</label>
          <input type="password" name="register-password" id="register-password" placeholder="Password" formControlName="password">
          <div *ngIf="registerForm.get('password')?.errors && (registerForm.get('password')?.dirty || registerForm.get('password')?.touched)" class="error">
            <div *ngIf="registerForm.get('password')?.errors?.required">Password is required.</div>
            <div *ngIf="registerForm.get('password')?.errors?.minlength">Password must be at least 6 characters long.</div>
          </div>
