<div class="modal">
  <div class="modal-container">
    <div class="modal-left">
      <h1 class="modal-title">Welcome!</h1>
      <p class="modal-desc">Budget Planner Application where you can manage , control and observe your Financials</p>
      <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
        <div class="input-block">
          <label for="email" class="input-label">Email</label>
          <input type="email" name="email" id="email" placeholder="Email" formControlName="email">
          <div *ngIf="loginForm.get('email')?.errors && (loginForm.get('email')?.dirty || loginForm.get('email')?.touched)" class="error">
            <div *ngIf="loginForm.get('email')?.errors?.required">Email is required.</div>
            <div *ngIf="loginForm.get('email')?.errors?.email">Invalid email format.</div>
          </div>
        </div>
        <div class="input-block">
          <label for="password" class="input-label">Password</label>
          <input type="password" name="password" id="password" placeholder="Password" formControlName="password">
          <div *ngIf="loginForm.get('password')?.errors && (loginForm.get('password')?.dirty || loginForm.get('password')?.touched)" class="error">
            <div *ngIf="loginForm.get('password')?.errors?.required">Password is required.</div>
          </div>
        </div>
        <div class="modal-buttons">

          <button class="input-button" type="submit" [disabled]="loginForm.invalid">Login</button>
        </div>
      </form>
      <p class="sign-up">Don't have an account? <a href="/register">Sign up now</a></p>
